import React, { useState } from "react";


const App=()=>{

  let newTime= new Date().toLocaleTimeString();
  const [ctime,setctime] = useState(newTime);

  const UpdateTime=()=>{
    let newTime= new Date().toLocaleTimeString();
    setctime(newTime);
  }

  setInterval(UpdateTime,1000)

  return(
    <>
      <h1> {newTime}</h1>
      <button onClick={UpdateTime}>Get Time</button>
    </>
  );
}

export default App;
